package com.example.animacoesimport androidx.compose.animation.core.Animatableimport androidx.compose.animation.core.LinearOutSlowInEasingimport androidx.compose.animation.core.RepeatModeimport androidx.compose.animation.core.animateFloatimport androidx.compose.animation.core.infiniteRepeatableimport androidx.compose.animation.core.rememberInfiniteTransitionimport androidx.compose.animation.core.tweenimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.sizeimport androidx.compose.runtime.Composableimport androidx.compose.runtime.LaunchedEffectimport androidx.compose.runtime.getValueimport androidx.compose.runtime.rememberimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.drawBehindimport androidx.compose.ui.draw.scaleimport androidx.compose.ui.graphics.Brushimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.graphics.drawscope.DrawScopeimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.Dpimport androidx.compose.ui.unit.dpimport com.example.animacoes.ui.theme.AnimacoesTheme@Composablefun CirclePulseAnimation(    modifier: Modifier = Modifier,    circleSize: Dp = 300.dp,    pulseCircleColor: Color = Color.Black,    innerCircleColor: Color = Color.Black,    innerCircleBorderColor: Color = Color.LightGray,    innerCircleBorderBrush: Brush? = null) {    val firstPulseAnimationDuration = 4000    val secondPulseAnimationDuration = 3000    val alphaDuration = 4000    val innerCircleRadius = circleSize.value * 0.8f    val firstPulseCircleRadius = remember { Animatable(initialValue = innerCircleRadius) }    val secondPulseCircleRadius = remember { Animatable(initialValue = innerCircleRadius) }    val alpha by rememberInfiniteTransition(label = "").animateFloat(        1f,        0f,        animationSpec = infiniteRepeatable(            animation = tween(durationMillis = alphaDuration),            repeatMode = RepeatMode.Restart        ),        label = ""    )    LaunchedEffect(key1 = firstPulseCircleRadius) {        firstPulseCircleRadius.animateTo(            targetValue = circleSize.value,            animationSpec = infiniteRepeatable(                animation = tween(                    durationMillis = firstPulseAnimationDuration,                    easing = LinearOutSlowInEasing                ),                repeatMode = RepeatMode.Restart            )        )    }    LaunchedEffect(key1 = secondPulseCircleRadius) {        secondPulseCircleRadius.animateTo(            targetValue = circleSize.value,            animationSpec = infiniteRepeatable(                animation = tween(                    durationMillis = secondPulseAnimationDuration,                    delayMillis = 1000,                    easing = LinearOutSlowInEasing                ),            )        )    }    Box(        modifier = modifier.size(circleSize).scale(1f).drawBehind {            pulseCircle(                radius = firstPulseCircleRadius.value,                color = pulseCircleColor,                alpha = alpha            )            pulseCircle(                radius = secondPulseCircleRadius.value,                color = pulseCircleColor,                alpha = alpha            )            innerCircleBorder(                radius = innerCircleRadius * 1.015f,                color = innerCircleBorderColor,                brush = innerCircleBorderBrush            )            innerCircle(                radius = innerCircleRadius,                color = innerCircleColor            )        },    )}fun DrawScope.pulseCircle(    radius: Float,    color: Color,    alpha: Float) {    drawCircle(        color = color,        radius = radius,        alpha = alpha    )}fun DrawScope.innerCircle(    radius: Float,    color: Color,) {    drawCircle(        color = color,        radius = radius,    )}fun DrawScope.innerCircleBorder(    radius: Float,    color: Color,    brush: Brush?,) {    if (brush != null){        drawCircle(            brush = brush,            radius = radius,        )    }else{        drawCircle(            color = color,            radius = radius,        )    }}@Composable@Preview(showBackground = true)fun CirclePulseAnimationPreview() {    AnimacoesTheme {        Column(            Modifier.fillMaxSize(),            horizontalAlignment = Alignment.CenterHorizontally,            verticalArrangement = Arrangement.Center        ) {            CirclePulseAnimation(                circleSize = 450.dp,                pulseCircleColor = Color(0x31F0AC68),                innerCircleColor = Color(0xFF535353),                innerCircleBorderColor = Color(0x5DFACC00),                innerCircleBorderBrush = Brush.verticalGradient(                    colorStops = arrayOf(                        Pair(                            0.2f,                            Color(0xD0DE7E24)                        ),                        Pair(                            1f,                            Color(0xD0FFCC23)                        ),                        Pair(                            0.2f,                            Color(0xD082FF23)                        )                    )                )            )        }    }}